import React, { Component } from "react";
import Spotify from "spotify-web-api-node";

const spotifyWebApi = new Spotify();
export default class Main extends Component {
  constructor() {
    super();
    const params = this.getHashParams();
    this.state = {
      values: [],
      loggedIn: params.access_token ? true : false,
    };
    if (params.access_token) {
      spotifyWebApi.setAccessToken(params.access_token);
    }
  }

  getHashParams() {
    var hashParams = {};
    var e,
      r = /([^&;=]+)=?([^&;]*)/g,
      q = window.location.hash.substring(1);
    while ((e = r.exec(q))) {
      hashParams[e[1]] = decodeURIComponent(e[2]);
    }
    return hashParams;
  }

  componentDidMount() {
    spotifyWebApi.searchArtists("Rolling Stone").then((response) => {
      this.setState({ values: response.body.artists.items });
    });
    spotifyWebApi.getArtistAlbums("43ZHCT0cAZBISjO8DG9PnE").then(
      function (data) {
        console.log("Artist albums", data.body);
      },
      function (err) {
        console.error(err);
      }
    );
  }

  render() {
    console.log(this.state.values);

    return (
      <div>
        <a href="http://localhost:8888/">
          <button> Login With Spotify </button>
        </a>
        <div>
          {this.state.values.map((album) => {
            return (
              <div>
                <p>{album.name}</p>
                <img src={album.images.url} />
                <p>Release Date</p>
              </div>
            );
          })}
        </div>
      </div>
    );
  }
}
