import React, { Component } from "react";
import Spotify from "spotify-web-api-node";

const spotifyWebApi = new Spotify();
export default class Main extends Component {
  constructor() {
    super();
    const params = this.getHashParams();
    this.state = {
      songs:[],
      loggedIn: params.access_token ? true : false,
      search: "",
      nowPlaying: {
        name: "Not Checked",
        image: "",
      },
    };
    if (params.access_token) {
      spotifyWebApi.setAccessToken(params.access_token);
    }
  }

  getHashParams() {
    var hashParams = {};
    var e,
      r = /([^&;=]+)=?([^&;]*)/g,
      q = window.location.hash.substring(1);
    while ((e = r.exec(q))) {
      hashParams[e[1]] = decodeURIComponent(e[2]);
    }
    return hashParams;
  }
  getNowPlaying() {
    spotifyWebApi.searchArtists("Love").then(
      function (data) {
        this.setState({songs:data.body.items})

        console.log('Search artists by "Love"', data.body);
      },
      function (err) {
        console.error(err);
      }
    );
  }

  render() {
    console.log(this.state.songs)
    return (
      <div>
        <a href="http://localhost:8888/">
          <button> Login With Spotify </button>
        </a>

        <div>Now nowPlaying {this.state.nowPlaying.name}</div>
        <img src={this.state.nowPlaying.img} />

        <input
          value={this.state.search}
          onChange={(e) => this.setState({ search: e.target.value })}
        />
        <button onClick={() => this.getNowPlaying()}>Get Now Playing</button>
      </div>
    );
  }
}
